import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';

import { NgxSpinnerService } from 'ngx-spinner';
import { WebcamImage, WebcamInitError, WebcamUtil } from 'ngx-webcam';

import { Observable, Subject } from 'rxjs';
import { Global } from '../../shared/global';
import { DeviceDetectorService } from 'ngx-device-detector';

@Component({
  selector: 'app-identificacao',
  templateUrl: './identificacao.component.html',
  styleUrls: ['./identificacao.component.css']
})
export class IdentificacaoComponent implements OnInit {
  // CAMERA ITENS
  public showWebcam = true; // ligar / desligar a webcam
  public allowCameraSwitch = true;
  public multipleWebcamsAvailable = false;
  public deviceId: string;
  public errors: WebcamInitError[] = [];
  public webcamImage: WebcamImage = null; // ultima snapshot
  public videoOptions: MediaTrackConstraints = { width: {ideal: 250}, height: {ideal: 250} };

  // gatilho de snapshot da webcam
  private trigger: Subject<void> = new Subject<void>();
  // mudar para a webcam seguinte / anterior / espec√≠fica; true/false: para frente/voltar, string: deviceId
  private nextWebcam: Subject<boolean|string> = new Subject<boolean|string>();

  constructor(private route: Router, private spinner: NgxSpinnerService,
    private deviceService: DeviceDetectorService) {}

  ngOnInit() {
    this.spinner.show();
    if (this.deviceService.isMobile()) {
      this.route.navigate(['/cliente']);
      Global.IMAGE_BASE_64 = "";
    }
    if(this.deviceService.isDesktop()) {}
    WebcamUtil.getAvailableVideoInputs()
    .then((mediaDevices: MediaDeviceInfo[]) => {
      this.multipleWebcamsAvailable = mediaDevices && mediaDevices.length > 1;
    });
  }

  // CAMERA METHODS
  public triggerSnapshot(): void {
    this.trigger.next();
  }

  public toggleWebcam(): void {
    this.showWebcam = !this.showWebcam;
  }

  public cameraWasSwitched(deviceId: string): void {
    console.log('active device: ' + deviceId);
    this.deviceId = deviceId;
    if (this.deviceId) {
      setTimeout(() => {
        this.triggerSnapshot();
      }, 3000);
    }
  }

  public handleImage(webcamImage: WebcamImage): void {
    this.webcamImage = webcamImage;
    console.log(this.webcamImage);
    Global.IMAGE_BASE_64 = webcamImage.imageAsBase64;
    if (Global.IMAGE_BASE_64 !== '') {
      this.spinner.hide();
      this.toggleWebcam();
      this.route.navigate(['/cliente']);
    }
  }

  public showNextWebcam(directionOrDeviceId: boolean|string): void {
    // true => avan√ßar em dispositivos
    // false => voltar em dispositivos
    // string => move para o dispositivo de acordo com seu deviceId
    this.nextWebcam.next(directionOrDeviceId);
  }

  public get triggerObservable(): Observable<void> {
    return this.trigger.asObservable();
  }

  public get nextWebcamObservable(): Observable<boolean|string> {
    return this.nextWebcam.asObservable();
  }

  public handleInitError(error: WebcamInitError): void {
    this.errors.push(error);
  }
}
